parameters.jsCode:
// Parse and format the form data
const formData = $input.item.json.body || $input.item.json;

// Format the initial message
const message = `Hola ${formData.customer_name},\n\nSomos del Banco de Córdoba. Le contactamos sobre su saldo pendiente de $${formData.debt_amount} ARS con vencimiento el ${formData.due_date}.\n\n¿Le gustaría hablar sobre opciones de pago?`;

return {
  customer_name: formData.customer_name,
  phone_number: formData.phone_number,
  debt_amount: formData.debt_amount,
  due_date: formData.due_date,
  initial_message: message,
  timestamp: new Date().toISOString()
};

